import React, { useEffect, useState } from "react";
import Navheader from "./Navheader";

class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expand: "false"
    };
    this.renderButton = this.renderButton.bind(this);
  }

  componentDidMount() {
    window.addEventListener("resize", this.renderButton);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.renderButton);
  }

  renderButton = () => {
    console.log(window.innerWidth);
    var buton = <div></div>;
    if (window.innerWidth <= 1000) {
      buton = (
        <div style={styles.button}>
          <Navheader></Navheader>
        </div>
      );
    }
    return buton;
  };

  render() {
    return (
      <div>
        <div className="Sidebar">
          <p className="Text-1 Name">KEXIN ZHENG</p>
          <div className="Menu">
            <p className="Text-2">ABOUT</p>
            <hr></hr>
            <div className="Menu-group">
              <a href="#" className="Text-2">
                CODE
              </a>
              <div className="Menu-2">
                <p className="Text-3">WEB</p>
                <p className="Text-3">OTHER</p>
              </div>
            </div>
            <div className="Menu-group">
              <p className="Text-2">VISUAL</p>
              <div className="Menu-2">
                <p className="Text-3">GRAPHIC</p>
                <p className="Text-3">PHOTO/VIDEO</p>
                <p className="Text-3">INSTALLATION</p>
                <p className="Text-3">DRAWING</p>
              </div>
              <div className="Menu-group">
                <p className="Text-2">RESUME</p>
              </div>
              <div className="Menu-group">
                <p className="Text-2">GITHUB</p>
              </div>
            </div>
          </div>
          <div className="footer">
            <p className="Text-footer">Created by Kexin ZHENG @ 2020</p>
          </div>
        </div>
      </div>
    );
  }
}

export default Sidebar;

const styles = {
  button: {
    display: "block",
    backgroundColor: "pink",
    position: "absolute",
    top: 20,
    left: 20
  }
};
