import React from "react";
import Sidebar from "./Sidebar";
import Navheader from "./Navheader";

class SidebarContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expand: "false"
    };
    this.shout = this.shout.bind(this);
  }

  ismobile = () => {
    if (window.innerWidth <= 800) return true;
    else return false;
  };

  shout = () => {
    if (window.innerWidth < 1000 && this.state.expand) {
      this.setState({
        expand: "false"
      });
    } else {
      this.setState({
        expand: "true"
      });
    }
  };

  componentDidMount() {
    window.addEventListener("resize", this.shout);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.shout);
  }

  renderSidebar() {
    return (
      <div>
        {this.state.expand}
        <Sidebar></Sidebar>
      </div>
    );
  }
  renderButton() {
    return (
      <div className="Navheader" onClick={this.clickExpand()}>
        <Navheader></Navheader>
      </div>
    );
  }

  renderThis() {
    if (window.innerWidth >= 1000 || this.expand) {
      {
        return this.renderSidebar();
      }
    } else {
      return this.renderButton();
    }
  }

  clickExpand() {
    console.log(this.state.expand);
    this.setState(prevState => ({
      expand: !prevState.expand
    }));
  }

  render() {
    return <div>{this.renderThis()}</div>;
  }
}

export default SidebarContainer;
