import React from "react";
import p5 from "p5";

const P5 = ({ setup, draw }) => {
  const sketch = p5 => {
    p5.setup = () => setup(p5);
    p5.draw = () => draw(p5);
  };
  new p5(sketch);

  return <></>;
};

function Background() {
  function setup(p5) {
    p5.createCanvas(p5.random(150, 300), 200);
    p5.frameRate(30);
    p5.background(0);
    p5.rectMode(p5.CENTER);
  }
  function draw(p5) {
    p5.background(25);
    p5.fill("yellowgreen");
    p5.text("mouseY : " + p5.mouseY.toFixed(2), 50, 30);
    p5.ellipse(100, 100, 50 + p5.cos(p5.frameCount * 0.2) * 20);
    p5.push();
    p5.fill("crimson");
    p5.translate(p5.mouseX, p5.mouseY);
    p5.rotate(p5.radians(p5.frameCount));
    p5.rect(0, 0, 80, 80);
    p5.pop();
  }
  return (
    <div className="App">
      <h2>implementing p5.js in React</h2>
      <P5 setup={setup} draw={draw} />
      <h1>fggewgerw</h1>
    </div>
  );
}

export default Background;
